/* Carrossel de Posts Relacionados*/
$(window).load(function() {
  $('.flexslider').flexslider({
    animation: "slide",
    animationLoop: true,
    controlNav: false,
    itemWidth: 240,
    prevText: "",
    nextText: "",
    move: 1
  });
});

$(function() {
    /* AJAX PARA PGINA DE ACESSOS */
    $('.submit-form-quest').click(function(e) {
        e.preventDefault();
        var area = $(this).val();
        $.ajax({
            type: 'GET',
            url: 'acesso.php',
            data: {area: area},
            success: function(data) {
                if (data == 'inserido') {
                    $("#formFull").addClass('hide');
                    $("#formPopup").addClass('hide');
                }
            }
        });
    });
    
    /* AJAX PARA PGINA DE NEWSLETTER */
    $('#msg').click(function() {
        $('#news-email').val('');
        $('#newsletter').show('fast');
        $('#newsletter').after('<div class="alert alert-success" id="msg"  onclick="hideMsg()"><button type="button" class="close" data-dismiss="alert"></button>Email cadastrado com sucesso</div>');
    });

    function hideMsg() {
        $('#news-email').val('');
        $('#newsletter').show('fast');
        $('#newsletter').after('<div class="alert alert-success" id="msg"  onclick="hideMsg()"><button type="button" class="close" data-dismiss="alert" onclick="hideMsg()"></button>Email cadastrado com sucesso</div>');
    }
    $('#news-submit').click(function(e){
        e.preventDefault();
        var email = $('#news-email').val();
        $.ajax({
            data: {email: email},
            url: 'news.php',
            success: function(data) {
                if (data == 'Este no  um email vlido!') {
                    $('#msg').removeClass('alert-success').addClass('alert-danger').html('<button type="button" class="close" data-dismiss="alert" onclick="hideMsg()"></button>' + data);
                    $('#newsletter').hide();
                    $('#msg').show('fast');
                } else {
                    $('#newsletter').hide();
                    $('#msg').show('fast');
                }
            }
        });
    });      
    
    /* FOCUS NA BUSCA */
    $("#drop-busca").click(function() {
        setTimeout(function() {
            $("#input-busca").focus();
        }, 100);
    });
    
    /* MENU RESPONSIVO */
    $('#simple-menu').click(function() {
        setTimeout(function() {
            if ($('#menu').hasClass('mm-opened')) {
                $('.navbar-fixed-top').attr('style', 'position:static');
            }
            if (!$('#menu').hasClass('mm-opened')){
                $('.navbar-fixed-top').attr('style', 'position:fixed');
            }
        }, 100);
    });    
});